import { Decoder } from "../";

describe("Decoder", () => {
  test("複雑なやつ", () => {
    const input =
      "";
    const actual = Decoder.decode(input);
    expect(actual).toEqual({
      a: [
        1,
        null,
        256,
        {
          あ: [undefined, false],
          あ0z: {
            s:
              "ああああああああああああああああああああああああああああああああああああああああ",
          },
        },
        -5000000000,
      ],
    });
  });
});
